declare module '*.module.css' {
    const content: { [className: string]: string };
    export default content;
}

declare module "*.svg" {
    const content: any;
    export default content;
}

declare module "*.png" {
    const content: any;
    export default content;
}

declare module "*.mp4" {
    const content: any;
    export default content;
}
